        </div>
        <input class="input email" type="email" v-model="change.login">
    </div>
    <div class="buttonbar">
      <my-button @click="patchLogin()">Изменить</my-button>
      <my-button @click="showHideLogin">Закрыть</my-button>
      <!-- <button class="button" @click="patchLogin()">Изменить</button> -->
      <!-- <button class="button" @click="showHideLogin">Закрыть</button> -->
    </div>
  </div>
</template>

<script>

import { mapActions, mapGetters, mapMutations } from 'vuex'

export default {
  data(){
    return{
      change:{
        login:null
      }
    }
  },
  computed: mapGetters(["getUserInformation"]),
  methods:{
    ...mapMutations(['showHideLogin']),
    ...mapActions(['patch']),
    patchLogin(){
      if (this.change.login.length>3){
        this.patch(this.change)
        this.login=null
        this.showHideLogin()
        alert("Логин успешно изменен!")
      }else{
        alert("Необходимо ввести более 3х символов!")
        this.nullContemporaneity()
      }
    }
  }
}
</script>
