<template>
  <div class="registrationWindow">
    <div>
      <div>
        <h2>Регистрация на сайте GameGuru.ru</h2>
      </div>
      <hr>
      <div class="area email">
        <h4 class="title email">Введите желаемый Логин</h4>
        <div class="imageCell">
          <img class="img confirm" v-if="getLoginContemporaneity===true" src="https://freepikpsd.com/file/2019/10/green-v-png-5-Transparent-Images.png" alt="">
          <img class="img reject" v-if="getLoginContemporaneity===false" src="data:image/png;base64," alt="">
        </div>
        <input class="input email" type="email"
        v-model="user.login"

        >
      </div>

      <div class="area password">
        <h4 class="title password">Введите желаемый Пароль</h4>
        <div class="imageCell">
          <img class="img confirm" v-if="confirmed.password===true" src="https://freepikpsd.com/file/2019/10/green-v-png-5-Transparent-Images.png" alt="">
          <img class="img reject"  v-if="confirmed.password===123" src="data:image/png;base64," alt="">
        </div>
        <input class="input password" type="password"
        v-model="user.password"
        @input="checkPassword"
        >
      </div>

      <div class="area password_confirm">
        <h4 class="title password_confirm">Повторите Пароль</h4>
        <div class="imageCell">
          <img class="img confirm" v-if="confirmed.password_confirm===true" src="https://freepikpsd.com/file/2019/10/green-v-png-5-Transparent-Images.png" alt="">
          <img  class="img reject" v-if="confirmed.password_confirm===123" src="data:image/png;base64," alt="">
        </div>
        <input class="input password_confirm" type="password"
         v-model="user.password_confirm"
         @input="confirmPassword"
        >
      </div>

      <div class="area forname">
        <h4 class="title forname">Введите ваше Имя</h4>
        <div class="imageCell">
          <img class="img confirm" v-if="confirmed.forname===true" src="https://freepikpsd.com/file/2019/10/green-v-png-5-Transparent-Images.png" alt="">
          <img class="img reject" v-if="confirmed.forname===false" src="data:image/png;base64," alt="">      
        </div>
      <input class="input forname" type="text"
      v-model="user.forname"
      @input="confirmForname"
      >
      </div>

      <div class="area lastname">
        <h4 class="title lastname">Введите вашу Фамилию</h4>
        <div class="imageCell">
          <img class="img confirm" v-if="confirmed.lastname===true" src="https://freepikpsd.com/file/2019/10/green-v-png-5-Transparent-Images.png" alt="">
          <img class="img reject" v-if="confirmed.lastname===false" src="data:image/png;base64," alt="">      
        </div>
      <input class="input lastname" type="text"
      v-model="user.lastname"
      @input="confirmLastname"
      >
      </div>

      <div class="area">
        <h4 class="title">Введите ваш возраст</h4>
        <div>
        </div>
        <input class="input age" type="text"
      v-model="user.age"
      >
      </div>

      <div class="buttonbar">
        <button class="button"
        @click="registerUser"
        >Регистрация</button>
        <button class="button"
        @click="cleanAllInput"
        >Удалить всё</button>
        <button class="button"
        @click="closeAll"
        >Закрыть окно регистрации</button>
      </div>

    </div>
  </div>
</template>

<script>
import { mapMutations, mapActions } from 'vuex'
export default {
  data(){
    return{
      user:{
        login:null,
        password:null,
        password_confirm:null,
        forname:null,
        lastname:null,
        age:null
      },
      confirmed:{
        password:null,
        forname:null,
        lastname:null
      }
    }
  },
  // computed: {
  //   ...mapGetters(["getLoginContemporaneity"])
  //   },
  methods:{
    ...mapMutations(["findLogin","nullContemporaneity","closeAll"]),
    ...mapActions(['newUser']),
    // checkLogin(){
    //   console.log(this.user.login+this.user.login.length)

    //   if (this.user.login.length>3){
    //     this.findLogin(this.user.login)
    //   }else{
    //     this.nullContemporaneity()
    //   }
    // },
    checkPassword(){
      if (this.user.password.length<1){
        this.confirmed.password = null
      }else if(this.user.password.length<6){
        this.confirmed.password = false
      }else{
        this.confirmed.password = true
      }
    },
    confirmPassword(){
      if (this.user.password===this.user.password_confirm){
        this.confirmed.password_confirm = true
      }else{
        this.confirmed.password_confirm = false
      }
    },
    confirmForname(){
      if(this.user.forname.length>3){
        this.confirmed.forname = true
      }else{
        this.confirmed.forname = null
      }
    },
    confirmLastname(){
      if(this.user.lastname.length>3){
        this.confirmed.lastname = true
      }else{
        this.confirmed.lastname = null
      }
    },
    registerUser(){
      this.newUser(this.user)
      this.cleanAllInput()
      this.closeAll()

    },
    cleanAllInput(){
      this.user.login = null,
      this.user.password = null,
      this.user.password_confirm = null,
      this.user.forname = null,
      this.user.lastname = null,
      this.confirmed.password = null,
      this.confirmed.password_confirm = null,
      this.confirmed.lastname = null,
      this.confirmed.forname = null,
      this.nullContemporaneity()
    }
  }
}
</script>

